import React, { useRef, useState, useEffect } from 'react';
import './Messageboard.css'
import MessageList from './MessageList';

function Messageboard() {

    const [messages, setMessages] = useState(null);
    const [loading, setLoading] = useState(null); 
    const [error, setError] = useState(null);

    const nameInput = useRef();
    const messageInput = useRef(); 

    //submit 버튼 눌렸을 때 일어나는 것들 처리 
    const test = () => {
        alert("name : "+nameInput.current.value+" message : "+messageInput.current.value);
        console.log(nameInput.current.value);
    }

    //메시지 불러오기 위한 함수 
    const getMessages = async () => {
        try{

            setError(null);
            setMessages(null);

            setLoading(true);
            const response = await axios.get('http://localhost:80/message/read');
            setMessages(response.data);


        } catch(e) {
            console.log("에러 발생 : " +e);
            setError(e);
        }
        setLoading(false);
    };

    useEffect(() => {
        getMessages();
    }, []);


    return(
        <div className="messageBoard">
            <div className="messageBox">  
                <input placeholder="name" ref={nameInput}/>
                <input placeholder="message" ref={messageInput}/>
                <button onClick={test}>submit</button>
            </div>
            <MessageList error={error} loading={loading} messages={messages}/>
        </div>
    );
}

export default Messageboard;
